import React, {Component} from 'react';
import Players from './Players';
import CreationPlayer from './CreationPlayer'

class App extends Component {
	constructor(){
		super()
		this.state = {
			newPlayer: "Alucard",
			age: "666",
			newPlayer2: "",
			age2: ""

		}
	}

	changeTextName = (event) =>{
		this.setState({newPlayer2: event.target.value});
		this.setState({age2: event.target.value});
	}

	changeTextAge = (event) =>{
		this.setState({newPlayer2: event.target.value});
		this.setState({age2: event.target.value});
	}

	pushButton = (event) =>{
		this.setState({newPlayer: this.state.newPlayer2});
		this.setState({age: this.state.age2});		
	}

	render(){
		return(
			<div>
				<h1>Tennis Players</h1>
				<Players />
				<CreationPlayer onChange={this.changeTextName} onClick={this.pushButton}/>
				<h5>{this.state.newPlayer}</h5>
				<h5>{this.state.age}</h5>
			</div>
		)		
	}
}

export default App;
